import React, { Component } from 'react';
import axios from 'axios';

export default class Dashboard extends Component {
    constructor(props) {
        super(props);

        this.state = {dashboard: []};

        this.logout = this.logout.bind(this)
        this.expenses = this.expenses.bind(this)
    }

    logout(e){
      e.preventDefault();

      localStorage.removeItem('token');
      localStorage.removeItem('username');

      window.location = '/';
    }

    expenses(e) {
      e.preventDefault();
      window.location = '/dashboard/expenses'
    }

    componentDidMount() {
        const token = localStorage.getItem('token');

        axios.get('http://localhost:5000/dashboard/', {
          headers: {
            "auth-token": token 
          }
        })
          .then(response => {
            console.log(response.data)
            this.setState({ dashboard: response.data.data.user.username})
            localStorage.setItem('username', this.state.dashboard)
          })
          .catch((error) => {
            console.log(error);
          })
    }

    render() {
        var dashboard
        var button
        if(localStorage.getItem('token')) {
          dashboard = this.state.dashboard
          button = 'Logout'
        }
        else {
          dashboard = 'Please login first'
          button = 'Login'
        }
        return (
            <div>
                {dashboard}
                <br />
                <button onClick={this.expenses}>Expenses List</button>
                <br />
                <button onClick={this.logout}>{button}</button>
            </div>
        )
    }
}
