import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

var incomes = 0;
var expenses = 0;

const Expense = props => (
    <tr>
        <td>{props.expense.username}</td>
        <td>{props.expense.description}</td>
        <td>{props.expense.type == "+" 
            ? <span style={{color: 'green'}}>{props.expense.amount}</span>
            : <span style={{color: 'red'}}>{props.expense.amount}</span>}</td>
        <td>{props.expense.date.substring(0,10)}, {props.expense.date.substring(12,19)}</td>
        <td>
        <Link to={"/dashboard/expenses/edit/"+props.expense._id}>edit</Link> | <a href="/dashboard/expenses" onClick={() => { props.deleteExpense(props.expense._id) }}>delete</a>
        </td>
    </tr>
)

export default class ExpensesList extends Component {
    constructor(props) {
        super(props);

        this.deleteExpense = this.deleteExpense.bind(this);
        // this.sort = this.sort.bind(this);

        this.state = { 
            expenses: [],
            sorted: false,
        };
    }

    componentDidMount() {
        const token = localStorage.getItem('token');
        axios.get('http://localhost:5000/dashboard/expenses/', {
            headers: {
              "auth-token": token 
            }
          })
            .then(res => {
                this.setState({ expenses: res.data });
            })
            .catch(err => {
                console.log(err)
            })
    }

    deleteExpense(id) {
        axios.delete('http://localhost:5000/dashboard/expenses/' + id)
            .then(res => console.log(res.data))

        this.setState({
            expenses: this.state.expenses.filter(el => el._id !== id)
        })
    }

    // sort(e) { // sort for the first one to the last one
    //     const sorted = this.state.expenses.sort((first, second) => {
    //         if (this.state.sorted === true) {
    //             if (first.date > second.date) return 1
    //                 return -1
    //         } else {if (first.date > second.date) return -1
    //                 return 1
    //         }
    //     })
    //     this.setState({ expenses: sorted, sorted: !this.state.sorted });
    //     incomes = 0;
    //     expenses = 0;
    // }

    exerciseList() {

        return this.state.expenses.map(eachExpense => {
            return <Expense expense={eachExpense} deleteExpense={this.deleteExpense}
                        key={eachExpense._id} />;
        })
    }

    summary() {
        return (
            <tr>
                <td>{this.income()}</td>
                <td>{this.expense()}</td>
                <td>{incomes - expenses}</td>
            </tr>
        )
    }

    income() {
        this.state.expenses.map(eachExpense => {
            if (eachExpense.type === '+')
                incomes += Number(eachExpense.amount)
        })
        return incomes
    }

    expense() {
        this.state.expenses.map(eachExpense => {
            if (eachExpense.type === '-')
                expenses += Number(eachExpense.amount)
        })
        return expenses;
    }

    render() {
        return (
        <div>
            <div className='container'>
                <h3>Logged Expenses</h3>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Amount ($)</th>
                            <th>Date <button >↑↓</button></th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.exerciseList() }
                    </tbody>
                </table>
            </div>
            <br />
            <div className='container'>
                <h3>Summary</h3>
                <table className='table'>
                    <thead className='thead-light'>
                        <tr>
                            <th>Income</th>
                            <th>Expense</th>
                            <th>Available</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.summary() }
                    </tbody>
                </table>
            </div>
        </div>
        )
    }
}
