import React, { Component } from 'react';
import axios from 'axios';

export default class EditExpense extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeAmount = this.onChangeAmount.bind(this);
    this.onChangeType = this.onChangeType.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      description: '',
      amount: 0,
      type: '',
      date: new Date(),
      users: []
    }
  }

  componentDidMount() {
    const token = localStorage.getItem('token')
    axios.get('http://localhost:5000/dashboard/expenses/'+this.props.match.params.id, {
        headers: {
          "auth-token": token 
        }
      })
      .then(response => {
        this.setState({
          username: localStorage.getItem('username'),
          description: response.data.description,
          amount: response.data.amount,
          type: response.data.type,
          date: new Date(response.data.date)
        })   
      })
      .catch(function (error) {
        console.log(error);
      })

    axios.get('http://localhost:5000/users/')
      .then(response => {
        this.setState({ users: response.data.map(user => user.username) });
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  onChangeAmount(e) {
    this.setState({
      amount: e.target.value
    });
  }

  onChangeType(e) {
    this.setState({
      type: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const expense = {
      username: this.state.username,
      description: this.state.description,
      amount: this.state.amount,
      type: this.state.type,
      date: this.state.date,
    };

    console.log(expense);

    const token = localStorage.getItem('token');
    axios.post('http://localhost:5000/dashboard/expenses/update/'+this.props.match.params.id , {
        headers: {
          "auth-token": token 
        }
      }, expense)
      .then(res => console.log(res.data));
    
    window.location = '/';
  }

  render() {
    return (
      <div className='container'>
        <h3>Edit Expense Log</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group"> 
            <label>Username: </label>
            <select
                className="form-control"
                value={this.state.username}
                onChange={this.onChangeUsername}>
                {
                  this.state.users.map(function(user) {
                    return <option 
                      key={user}
                      value={user}>{user}
                      </option>;
                  })
                }
            </select>
          </div>
          <div className="form-group"> 
            <label>Description: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.description}
                onChange={this.onChangeDescription}
                />
          </div>
          <div className="form-group">
            <label>Amount ($): </label>
            <input 
                type="text" 
                className="form-control"
                value={this.state.amount}
                onChange={this.onChangeAmount}
                />
          </div>
          <div className="form-group"> 
            <label>Type: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.type}
                onChange={this.onChangeType}
                />
          </div>
          <div className="form-group">
            <input type="submit" value="Edit Expense Log" className="btn btn-primary" />
          </div>
        </form>
      </div>
    )
  }
}
